import { React, useState, useEffect } from "react";
import CategoryFilter from "./CategoryFilter";
import About from "../pages/About";

const categories = [
  {
    name : "투표",
    value : "vote"
  },
  {
    name : "어워즈 설명",
    value : "awards-desc"
  },
  {
    name : "후보 설명",
    value : "meme-desc"
  },
];

const Category = () => {
    const [category, setCategory] = useState("vote");
    const [content, setContent] = useState("");

    const currentContent = (currentCategory) => {
        if (currentCategory === 'vote'){
          setContent(<About/>);
        }
    };

  useEffect(()=> {
    setCategory(category);
    setContent(category);
    currentContent(category);
  }, [category]);

  return (
    <div>
      <CategoryFilter
        categories={categories}
        category={category}
        setCatecory={setCategory}
      />
            {content}
        </div>
  );
};

export default Category;
