import { useEffect, useState, React } from "react";
import { Link } from 'react-router-dom';
import '../components/About.css';
import ReactTypingEffect from 'react-typing-effect';
import { getData, setData } from "../Api";
import { useCookies } from "react-cookie";
import moment from "moment/moment";
import { collection, getFirestore, getDocs, getDoc, setDoc } from "firebase/firestore";

export default function About() {
    const [idolVoted, setIdolVoted] = useState(false);
    const [poseVoted, setPoseVoted] = useState(false);
    
    const COOKIE_KEYS = 'isVoted';
    const [cookies, setCookies] = useCookies([COOKIE_KEYS]);

    async function getData(memeName){
        const db = getFirestore();
        const memeRef = doc(db, "meme-awards", memeName);
        const memeSnap = await getDoc(memeRef); 
        
        if (memeSnap.exists()) {
            console.log("이미 있음!");
        } else {
            console.log(memeRef);
        try {
            await setDoc(memeRef, {
                name : memeName
            });
            console.log("Document written");
          } catch (e) {
            console.error("Error adding document: ", e);
          }
    }
    }


    const setData = () => {
        console.log(idolVoted, poseVoted);
        const expireDay = moment();
        expireDay.add(10, 'seconds');
        
        if (idolVoted && poseVoted){
            if (cookies[COOKIE_KEYS]){
                alert("이미 참여함!");
            } else {
                setCookies(COOKIE_KEYS, 'true' , {
                        path: '/',
                        expires: expireDay.toDate(),
                    });
            }
        } else{
            alert('모든 부문에 투표해주세요!');
        } 
    }

    const checkIdolVote = (idolName) => {
        setIdolVoted(idolName);
        console.log(idolVoted);
    }

    const checkPoseVote = (poseName) => {
        setPoseVoted(poseName);
        console.log(poseVoted);
    }

    return(
        <>
            <div className="idol-nomination">
                <div className="idol1" onClick={() => checkIdolVote('idol1')}>
                    아이돌1
                </div> 
                <div className="idol1">
                    아이돌2
                </div>
                <div className="idol1">
                    아이돌3
                </div>
                <div className="idol1">
                    아이돌4
                </div>   
            </div>
            <div className="pose-nomination">
                <div className="pose1" onClick={() => checkPoseVote('pose1')}>
                    밈포즈1
                </div> 
                <div className="pose1">
                    밈포즈2
                </div>
                <div className="pose1">
                    밈포즈3
                </div>
                <div className="pose1">
                    밈포즈4
                </div>   
            </div>

            <div className="HomeBoxIntro" onClick={() => setData()}>
                {cookies[COOKIE_KEYS] ?                         
                <h1>
                    이미 참여했습니다!
                </h1> : (
                <h1>
                    투표하기
                </h1>
                )}
            </div>
            <div className="AboutContainer">
            <div className="AboutContainerTitle">너 때문에 밈치겠어</div>
                <div className="AboutBox">
                <div className="WhoAmI">
                    <ReactTypingEffect speed="100" typingDelay="1000" eraseSpeed="100" eraseDelay="9000" 
                    text={["2022 밈어워즈 가보자고"]}/><br/><br/>
                    <ReactTypingEffect speed="100" typingDelay="1000" eraseSpeed="100" eraseDelay="9000" 
                    text={["밈어워즈 밈어워즈 밈어워즈"]}/><br/><br/>
                </div> 
                </div>
            </div>
        </>
    );
}
