import React, {useEffect, useState } from "react";
import { collection, addDoc, getDoc, getFirestore } from "firebase/firestore";
import { getDocs, doc } from 'firebase/firestore/lite';


export async function getData() {
    const votingButton = document.getElementsByClassName('button');

    const db = getFirestore();
    const colRef = collection(db, 'meme-awards');
    
    const data = doc(colRef, "meme-awards", 'nomi1');
    const dataSnapShot = await getDocs(data);

    if (!dataSnapShot.exists()) {
        console.log('aa');
        return null
        }
        return dataSnapShot.data();
}
