import React, {useEffect, useState } from "react";
import '../components/Home.css';
import styled from "styled-components";

export default function Home() {
    const [isHomeContainerScrolled, setIsHomeContainersScrolled] = useState(false);    // Home의 Top 값
    const homeContainer = document.getElementsByClassName('HomeContainer');
    const [homeContainerFromTop, setHomeContainerFromTop] = useState(0);

    const [hour, setHour] = useState(23 - new Date().getHours());
    const [minute, setMinute] = useState(59 - new Date().getMinutes());
    const [second, setSecond] = useState(59 - new Date().getSeconds());

    const diffTime = () => {
        const voteEndTime = new Date("2022-12-08");
        const todayTime = new Date();
        const remainingTime = voteEndTime - todayTime;

        const diffDay = Math.floor(remainingTime / (1000*60*60*24));
        const diffHour = Math.floor((remainingTime / (1000*60*60)) % 24);
        const diffMin = Math.floor((remainingTime / (1000*60)) % 60);
        const diffSec = Math.floor(remainingTime / 1000 % 60);

        console.log(diffDay, diffHour, diffMin, diffSec);
    }




    const showHomeContainer = () => {
        if(homeContainerFromTop <= 100){
            setIsHomeContainersScrolled(true);
        }
    }

    const listener = () => {
        if(homeContainer[0])
        {
            setHomeContainerFromTop(homeContainer[0].getBoundingClientRect().top);
        }
    }

    useEffect(() => {
            window.addEventListener('scroll', listener);
            showHomeContainer(homeContainerFromTop);
        }
    );


    
    return(
        <>
        <BannerImg src="https://www.banggooso.com/ms/meme-awards/main-logo.png"/>
            <div className={isHomeContainerScrolled ? 'HomeContainer HomeContainerScrolled' : 'HomeContainer'}>
            </div>
        </>
    )
}

const BannerImg = styled.img`
    width:60%;
    margin-left: 20%;
    margin-top: 15rem;
`
