    value : "vote"
  },
  {
    name : "어워즈 설명",
    value : "awards-desc"
  },
  {
    name : "후보 설명",
    value : "meme-desc"
  },
];

const qnaList = [
    {
      category: "category1",
      question: "what is that ? 1",
      answer: "this is react. 1",
