import React, {useEffect, useState } from "react";
import '../components/Home.css';
import Timer from "../components/Timer";
import { getFirestore, getDoc, doc } from "firebase/firestore";
import styled from "styled-components";


export default function Home() {
    let totalVoteCount = 0;
    const getTotalVoteCount = async() => {
        const db = getFirestore();
        const totalRef = doc(db, "TOTAL", "COUNT");
        const totalSnap = await getDoc(totalRef);

        totalVoteCount = totalSnap.data();
        
        console.log(totalVoteCount);
    }

    setInterval(() =>{
            getTotalVoteCount();
        }, 1000);
    

    return(
        <>
        <BannerImg src="https://www.banggooso.com/ms/meme-awards/main-logo.png"/>
            <div className = 'HomeContainer HomeContainerScrolled'>
                <HomeText>
                    "WITH MEME WITH ME"<br/>
                    올 한 해, 당신에게 최고의 밈은 무엇인가요?
                    <br/><br/>
                    각 분야별 시상과 올해의 밈 시상은<br/>
                    2022.12.21 20시 유튜브 '이십세들'에서<br/>
                    온라인 시상식으로 진행됩니다.
                </HomeText>
            <Timer/>
            </div>
            {totalVoteCount}
        </>
    )
}

const BannerImg = styled.img`
    width: 50%;
    margin-left: 25%;
    margin-top: 15%;
`;

const HomeText = styled.div`
    text-align: center;
    font-size: 1.6rem;
    margin-bottom: 5rem;
`
