import { useEffect, useState, React } from "react";
import '../components/About.css';
import { useCookies } from "react-cookie";
import moment from "moment/moment";
import { getFirestore, getDoc, setDoc, doc, updateDoc, increment } from "firebase/firestore";
import styled from "styled-components";

export default function About() {
    const [idolVoted, setIdolVoted] = useState("");
    const [poseVoted, setPoseVoted] = useState("");
    const [btnActive, setBtnActive] = useState(false);
    const [isvoted, setIsVoted] = useState(false);

    const COOKIE_KEYS = 'isVoted';
    const [cookies, setCookies] = useCookies([COOKIE_KEYS]);

    const data = ["first", "second", "third"];

    function voteMeme(idolMeme, poseMeme){
        const db = getFirestore();
        const memeRefArray = [doc(db, "IDOL", idolMeme), doc(db, "POSE", poseMeme)];
        
        memeRefArray.forEach(async memeEle => {
            const memeSnap = await getDoc(memeEle); 
        
            if (memeSnap.exists()) {
                await updateDoc(memeEle, {
                    count: increment(1)
                });
            } else {
                console.log(memeEle);
            try {
                await setDoc(memeEle, {
                    count : 1
                });
                console.log("Document written");
                } catch (e) {
                    console.error("Error adding document: ", e);
                    }
            }
        });
        
    }

    const setData = () => {
        console.log(idolVoted, poseVoted);  // 디버깅용 코드
        const expireDay = moment();
        expireDay.add(10, 'seconds');
        
        if (idolVoted !== "" && poseVoted !== ""){
            if (cookies[COOKIE_KEYS]){
                alert("이미 참여함!");
            } else {
                voteMeme(idolVoted, poseVoted);
                setCookies(COOKIE_KEYS, 'true' , {
                        path: '/',
                        expires: expireDay.toDate(),
                    });
            }
        } else{
            alert('모든 부문에 투표해주세요!');
        } 
    }

    const checkIdolVote = (idolName) => {
        if (idolVoted === ""){
            setIdolVoted(idolName);
        }else{
            setIdolVoted("");
        }   
    }
    
    const checkPoseVote = (poseName) => {
        if (poseVoted === ""){
            setPoseVoted(poseName);
        }else{
            setPoseVoted("");
        }   
    }

    // 디버깅용 코드
    useEffect(() => {
        console.log(idolVoted, poseVoted);
    }, [idolVoted, poseVoted]);


    const ToggleItem = ({ discription, id }) => {
        const [toggleThisElement, setToggleThisElement] = useState(false);
        return (
          <div className="single-history" key={id}>
            <Nomination
              className="h-head"
              onClick={() => setToggleThisElement((prev) => !prev)}
            >
              click this btn for toggle h-info block {id}
            </Nomination>
      
            {toggleThisElement && <div className="h-info">{discription}</div>}
          </div>
        );
      };

    return(
        <>  
            <VoteTitle>
                아이돌 밈 부문
            </VoteTitle>
            <VoteBox>
            {data.map((d, id) => {
                return <ToggleItem id={id} discription={d} />;
            })} 
                <Nomination className="idol second" onClick={() => checkIdolVote('idol2')}>
                    아이돌2
                </Nomination>
                <Nomination className="idol third" onClick={() => checkIdolVote('idol3')}>
                    아이돌3
                </Nomination>
                <Nomination className="idol fourth" onClick={() => checkIdolVote('idol4')}>
                    아이돌4
                </Nomination>   
            </VoteBox>

            <VoteTitle>
                밈 포즈 부문
            </VoteTitle>
            <VoteBox>
                <Nomination onClick={() => checkPoseVote('pose1')}>
                    밈포즈1
                </Nomination> 
                <Nomination onClick={() => checkPoseVote('pose2')}>
                    밈포즈2
                </Nomination>
                <Nomination onClick={() => checkPoseVote('pose3')}>
                    밈포즈3
                </Nomination>
                <Nomination onClick={() => checkPoseVote('pose4')}>
                    밈포즈4
                </Nomination>   
            </VoteBox>

            <div className="HomeBoxIntro" onClick={() => setData()}>
                {cookies[COOKIE_KEYS] ?                         
                <h1>
                    이미 참여했습니다!
                </h1> : (
                <button>
                    투표하기
                </button>
                )}
            </div>
        </>
    );
}

const VoteTitle = styled.div`
    color: white;
    font-size: 2.5rem;
    text-align: center;
    margin-bottom: 2rem;
`
const VoteBox = styled.div`
    display: flex;
    flex-flow : row wrap;
    width: 90% !important;
    margin: auto;
    margin-bottom: 10rem;
    line-height: 5rem;
`
const Nomination = styled.button`
    width: calc(100%/2.2);
    color: black;
    font-size: 2rem;
    font-weight: bold;
    text-align: center;
    height: 7rem;
    line-height: 7rem;
    margin: 2rem 1rem;
    cursor: pointer;
    background: #ab9831;
}
`;
