import { React, useState, useEffect } from "react";
import CategoryFilter from "./CategoryFilter";
import About from "../pages/About";

const categories = [
  {
    name : "투표",
    value : "vote"
  },
  {
    name : "어워즈 설명",
    value : "awards-desc"
  },
  {
    name : "후보 설명",
    value : "meme-desc"
  },
];

const Category = () => {
    const [category, setCategory] = useState("vote");
    const [content, setContent] = useState(<About/>);
    
    const getQnACard = (currentCategory) => {
        if (currentCategory == 'vote'){
            console.log("vote");
        }

        return (
            <div className="faq-card">
                {currentCategory == 'vote' ? <About/> : <div>asd</div>}  
            </div>
        );
    };

  useEffect(()=> {
    setCategory(category);
  }, [category]);

  return (
    <div>
      <CategoryFilter
        categories={categories}
        category={category}
        setCatecory={setCategory}
      />
      <div className="fqa-parent">
        <div className="faq-list">
        </div>
      </div>
    </div>
  );
};

export default Category;
